    //create series, then add to database
    private void butAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butAddActionPerformed
        //create new series
        Series series = new Series();

        // get data from text fields
        series.setName(tfName.getText());
        series.setNumBooks(Integer.parseInt(tfNumBooks.getText()));

        Driver d = new Driver();

        if (series.addToDatabase()) {
            d.infoMessageNormal(series.getName() + " successfully added to the database.");
        } else {
            d.errorMessageNormal("Series could not be added.");
        }

        this.dispose();
    }//GEN-LAST:event_butAddActionPerformed

